%% Literal a, usando if elseif end
a=input('Ingrese el coeficiente a de (ax^2+bc+c=0): ');
b=input('Ingrese el coeficiente b de (ax^2+bc+c=0): ');
c=input('Ingrese el coeficiente c de (ax^2+bc+c=0): ');

if a==0 && b==0
    disp('La escuacion es degenerada');
elseif a==0 && b~=0
    fprintf('\nExiste raiz unica de la ecuacion: %f\n',-c/b);
else
    d=b^2-4*a*c;
    if d>=0
        raiz(1)=(-b+sqrt(d))/(2*a);
        raiz(2)=(-b-sqrt(d))/(2*a);
        fprintf('\nLa primera raiz de la ecuacion es: %f\n',raiz(1));
        fprintf('La segunda raiz de la ecuacion es: %f\n',raiz(2));
    else
        raiz=(-b+sqrt(d))/(2*a);
        fprintf('\nLa primera raiz de la ecuacion es: %f+%fj\n',real(raiz),abs(imag(raiz)));
        fprintf('La segunda raiz de la ecuacion es: %f-%fj\n',real(raiz),abs(imag(raiz)));
    end
end

%% Literal a, usando switch case end
a=input('Ingrese el coeficiente a de (ax^2+bc+c=0): ');
b=input('Ingrese el coeficiente b de (ax^2+bc+c=0): ');
c=input('Ingrese el coeficiente c de (ax^2+bc+c=0): ');

switch true
    case a==0 && b==0
        disp('La escuacion es degenerada');
    case a==0 && b~=0
        fprintf('\nLa raiz de la ecuacion es: %f\n',-c/b);
    otherwise
        d=b^2-4*a*c;
        if d>=0
            raiz(1)=(-b+sqrt(d))/(2*a);
            raiz(2)=(-b-sqrt(d))/(2*a);
            fprintf('\nLa primera raiz de la ecuacion es: %f\n',raiz(1));
            fprintf('La segunda raiz de la ecuacion es: %f\n',raiz(2));
        else
            raiz=(-b+sqrt(d))/(2*a);
            fprintf('\nLa primera raiz de la ecuacion es: %f+%fj\n',real(raiz),abs(imag(raiz)));
            fprintf('La segunda raiz de la ecuacion es: %f-%fj\n',real(raiz),abs(imag(raiz)));
        end
end